let pokemonRepository = (function () {
  let t = [],
    e = "https://pokeapi.co/api/v2/pokemon/?limit=150";
  function n(e) {
    "object" == typeof e && "name" in e
      ? t.push(e)
      : console.log("pokemon is not correct");
  }
  function o(t) {
    let e = t.detailsUrl;
    return fetch(e)
      .then(function (t) {
        return t.json();
      })
      .then(function (e) {
        (t.imageUrl = e.sprites.front_default),
          (t.height = e.height),
          (t.types = e.types);
      })
      .catch(function (t) {
        console.error(t);
      });
  }
  function i(t) {
    o(t).then(function () {
      c(t);
    });
  }
  function c(t) {
    document.querySelector(".modal-title").innerText = t.name;
    let e = document.querySelector(".modal-body");
    e.innerText = "";
    let n = document.createElement("img");
    n.classList.add("imagesize"), (n.src = t.imageUrl);
    let o = document.createElement("p");
    o.classList.add("height"),
      (o.innerText = "Height: " + t.height),
      e.append(n),
      e.append(o);
  }
  return {
    add: n,
    getAll: function () {
      return t;
    },
    loadList: function () {
      return fetch(e)
        .then(function (t) {
          return t.json();
        })
        .then(function (t) {
          t.results.forEach(function (t) {
            n({ name: t.name, detailsUrl: t.url });
          });
        })
        .catch(function (t) {
          console.error(t);
        });
    },
    loadDetails: o,
    addListItem: function (t) {
      let e = document.querySelector(".list-group"),
        n = document.createElement("li");
      n.classList.add("group-list-item");
      let o = document.createElement("button");
      (o.innerText = t.name),
        o.classList.add("btn", "btn-secondary", "btn-block"),
        o.setAttribute("data-target", "#pokedexmodal"),
        o.setAttribute("data-toggle", "modal"),
        n.appendChild(o),
        e.appendChild(n),
        o.addEventListener("click", () => {
          i(t);
        });
    },
    showDetails: i,
    showModal: c,
  };
})();
pokemonRepository.loadList().then(function () {
  pokemonRepository.getAll().forEach(function (t) {
    pokemonRepository.addListItem(t);
  });
});
